import './comp.css';

function Comp15() {

    function one() {
        let res = document.querySelector('.res');
        res.innerHTML = "hello";
    }
    function two() {
        let res = document.querySelector('.res');
        res.innerHTML = "again";
    }

    return (
        <div className="comp comp15">
            <h3><span>15</span>get text by onClick</h3>
            <button onClick={one}>PUSH1</button>
            <button onClick={two}>PUSH2</button>
            <div className='res'></div>
        </div>
    )
}

export default Comp15;
