import './comp.css';

function Comp09(props) {
    let content;
    let isAdmin;
    let checkBox = <input type="checkbox" value={'PUSH'} />

    if (checkBox.checked) {
        isAdmin = true;
    } else {
        isAdmin = false;
    }

    if (isAdmin) {
        content = <span>Active</span>;
    } else {
        content = <span>No Active</span>
    }

    return (
        <div className="comp comp08">
            <h3><span>08</span>condition for variable - true/false</h3>
            <li>{content}</li>
        </div>
    )
}

export default Comp09;
