import './comp.css';

function Comp17() {

    function func(event) {
        let res = document.querySelector('.comp17 > .res')
        console.log(event); // get event
        console.log(event.target); // get button where been click
        res.innerHTML = event;
    }

    return (
        <div className="comp comp17">
            <h3><span>17</span>event / SyntheticEvent</h3>
            <button onClick={func}>PUSH</button>
            <div className='res'></div>
        </div>
    )
}

export default Comp17;
