import './comp.css';

function Comp15() {

    function show(name) {
        let res = document.querySelector('.comp15 > .res');
        res.innerHTML = "hello";
    }

    return (
        <div className="comp comp15">
            <h3><span>15</span>get text by onClick</h3>
            <button onClick={show}>PUSH1</button>
            <button onClick={() => show('Sergio')}>show</button>
            <div className='res'></div>
        </div>
    )
}

export default Comp15;
